n.input-addon,\n.input-group-addon {\n  color: $text-color;\n}\n\n.has-warning {\n  .help-block,\n  .control-label,\n  .form-control-feedback {\n    color: $brand-warning;\n  }\n\n  .input-group-addon {\n    border: none;\n  }\n}\n\n.has-error {\n  .help-block,\n  .control-label,\n  .form-control-feedback {\n    color: $brand-danger;\n  }\n\n  .input-group-addon {\n    border: none;\n  }\n}\n\n.has-success {\n  .help-block,\n  .control-label,\n  .form-control-feedback {\n    color: $brand-success;\n  }\n\n  .input-group-addon {\n    border: none;\n  }\n}\n\n.form-control:focus {\n  @include box-shadow(none);\n}\n\n.has-warning,\n.has-error,\n.has-success {\n  .form-control:focus {\n    @include box-shadow(none);\n  }\n}\n\n// Navs =======================================================================\n\n.nav {\n  .open > a,\n  .open > a:hover,\n  .open > a:focus {\n    border-color: transparent;\n  }\n}\n\n.nav-tabs {\n  > li > a {\n    color: $text-color;\n  }\n}\n\n.nav-pills {\n  > li > a {\n    color: $text-color;\n  }\n}\n\n.pager {\n  a {\n    color: $text-color;\n  }\n}\n\n// Indicators =================================================================\n\n.label {\n  font-weight: 300;\n}\n\n.alert {\n  color: #fff;\n\n  a,\n  .alert-link {\n    color: #fff;\n  }\n}\n\n.close {\n  opacity: 0.4;\n\n  &:hover,\n  &:focus {\n    opacity: 1;\n  }\n}\n\n// Progress bars ==============================================================\n\n// Containers =================================================================\n\n.well {\n  @include box-shadow(none);\n}\n\na.list-group-item {\n\n  &.active,\n  &.active:hover,\n  &.active:focus {\n    border: none;\n  }\n\n  &-success {\n    &.active {\n      background-color: $state-success-bg;\n    }\n\n    &.active:hover,\n    &.active:focus {\n      background-color: darken($state-success-bg, 5%);\n    }\n  }\n\n  &-warning {\n    &.active {\n      background-color: $state-warning-bg;\n    }\n    \n    &.active:hover,\n    &.